CREATE OR REPLACE FUNCTION obter_valores_conpaci (nr_interno_conta_p bigint, nr_seq_protocolo_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

 
vl_retorno_w	double precision;
vl_pago_w	double precision;
vl_saldo_w	double precision;
vl_conta_w	double precision;
vl_titulo_w	double precision;
vl_glosa_w	double precision;

/* 
	P - valor pago 
	S - valor saldo 
	G - valor glosado 
*/
 
 

BEGIN 
 
vl_retorno_w	:= 0;
vl_pago_w	:= 0;
vl_saldo_w	:= 0;
vl_conta_w	:= 0;
vl_titulo_W	:= 0;
 
if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') then 
 
	select	sum((obter_dados_titulo_receber(x.nr_titulo,'VA'))::numeric ) 
	into STRICT	vl_titulo_w 
	from	titulo_receber x 
	where	((x.nr_seq_protocolo	= nr_seq_protocolo_p and coalesce(x.nr_interno_conta::text, '') = '') or (x.nr_interno_conta	= nr_interno_conta_p));
 
	vl_conta_w	:= obter_valor_conta(nr_interno_conta_p,0);
 
	select	coalesce(sum(b.vl_recebido),0), 
		vl_conta_w, 
		coalesce(sum(b.vl_glosa),0) 
		- coalesce(sum(b.vl_recebido),0) 
		- coalesce(sum(b.vl_descontos),0) 
		- coalesce(sum(b.vl_glosa),0) 
		+ vl_titulo_W 
	into STRICT	vl_pago_w, 
		vl_saldo_w, 
		vl_glosa_w 
	from	titulo_receber_liq b, 
		titulo_receber a 
	where	a.nr_titulo		= b.nr_titulo 
	and	((a.nr_seq_protocolo	= nr_seq_protocolo_p and coalesce(a.nr_interno_conta::text, '') = '') or (a.nr_interno_conta	= nr_interno_conta_p));
 
else 
 
	select	sum((obter_dados_titulo_receber(x.nr_titulo,'VA'))::numeric ) 
	into STRICT	vl_titulo_w 
	from	titulo_receber x 
	where	((x.nr_seq_protocolo	= nr_seq_protocolo_p and coalesce(x.nr_interno_conta::text, '') = '') or (x.nr_interno_conta	= nr_interno_conta_p));
 
	vl_conta_w	:= obter_valor_conta(nr_interno_conta_p,0);
 
	select	coalesce(sum(b.vl_recebido),0), 
    	vl_conta_w, 
		coalesce(sum(b.vl_glosa),0) 
	    - coalesce(sum(b.vl_recebido),0) 
    	- coalesce(sum(b.vl_descontos),0) 
	    - coalesce(sum(b.vl_glosa),0) 
	    + vl_titulo_w 
    into STRICT	vl_pago_w, 
        vl_saldo_w, 
		vl_glosa_w 
   	from	titulo_receber_liq b, 
    	titulo_receber a 
	where (a.nr_titulo		= b.nr_titulo) 
	and (a.nr_interno_conta 	= nr_interno_conta_p);
 
end if;
 
if (ie_opcao_p = 'P') then 
	vl_retorno_w := vl_pago_w;
elsif (ie_opcao_p = 'S') then 
	vl_retorno_w := vl_saldo_w;
elsif (ie_opcao_p	= 'G') then 
	vl_glosa_w	:= vl_glosa_w;
end if;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
