CREATE OR REPLACE FUNCTION obter_valores_conta_banco_data (nr_seq_conta_p bigint, dt_referencia_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p
VE - Valor de entradas
VS - Valor de saídas
VET - Valor de entradas	- Considera estorno com ação contrária (Estorno de débito = Crédito, Estorno de Crédito = Débito)
VST - Valor de saídas	- Considera estorno com ação contrária (Estorno de débito = Crédito, Estorno de Crédito = Débito)
*/
vl_retorno_w	double precision;
vl_estorno_w	double precision;
dt_inicial_w	timestamp;
dt_final_w	timestamp;


BEGIN

dt_inicial_w	:= trunc(dt_referencia_p,'dd');
dt_final_w	:= fim_dia(dt_referencia_p);

if (ie_opcao_p	= 'VE') then
	select	coalesce(sum(a.vl_transacao),0)
	into STRICT	vl_retorno_w
	from	transacao_financeira b,
		movto_trans_financ a
	where	b.ie_banco			= 'D'
	and	a.nr_seq_trans_financ		= b.nr_sequencia
	and	a.dt_transacao			between dt_inicial_w and dt_final_w
	and	a.nr_seq_banco			= nr_seq_conta_p;
elsif (ie_opcao_p	= 'VS') then
	select	coalesce(sum(a.vl_transacao),0)
	into STRICT	vl_retorno_w
	from	transacao_financeira b,
		movto_trans_financ a
	where	b.ie_banco			= 'C'
	and	a.nr_seq_trans_financ		= b.nr_sequencia
	and	a.dt_transacao			between dt_inicial_w and dt_final_w
	and	a.nr_seq_banco			= nr_seq_conta_p;
elsif (ie_opcao_p	= 'VET') then
	begin
	select	coalesce(sum(CASE WHEN coalesce(b.IE_SALDO_CB_ACAO_INVERSA,'N')='N' THEN  a.vl_transacao  ELSE CASE WHEN 	b.ie_banco='D' THEN  CASE WHEN coalesce(a.nr_seq_movto_orig::text, '') = '' THEN  a.vl_transacao  ELSE 0 END  WHEN 	b.ie_banco='C' THEN  CASE WHEN coalesce(a.nr_seq_movto_orig::text, '') = '' THEN  0  ELSE a.vl_transacao * -1 END   ELSE 0 END  END ),0)
	into STRICT	vl_retorno_w
	from	transacao_financeira b,
		movto_trans_financ a
	where	a.nr_seq_trans_financ		= b.nr_sequencia
	and	a.dt_transacao			between dt_inicial_w and dt_final_w
	and	a.nr_seq_banco			= nr_seq_conta_p;
	end;
elsif (ie_opcao_p	= 'VST') then
	begin
	select	coalesce(sum(CASE WHEN coalesce(b.IE_SALDO_CB_ACAO_INVERSA,'N')='N' THEN  a.vl_transacao  ELSE CASE WHEN 	b.ie_banco='C' THEN  CASE WHEN coalesce(a.nr_seq_movto_orig::text, '') = '' THEN  a.vl_transacao  ELSE 0 END  WHEN 	b.ie_banco='D' THEN  CASE WHEN coalesce(a.nr_seq_movto_orig::text, '') = '' THEN  0  ELSE a.vl_transacao * -1 END   ELSE 0 END  END ),0)
	into STRICT	vl_retorno_w
	from	transacao_financeira b,
		movto_trans_financ a
	where	a.nr_seq_trans_financ		= b.nr_sequencia
	and	a.dt_transacao			between dt_inicial_w and dt_final_w
	and	a.nr_seq_banco			= nr_seq_conta_p;
	end;
end if;

return vl_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
