SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valores_cot_compra ( nr_sequencia_p bigint, nr_cot_compra_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
vl_liquido_w	varchar(100);
vl_desconto_w	varchar(100);
vl_total_w	varchar(100);


/*

IE_TIPO_P

VL = Total do Valor Liquido
VD = Total do Valor de Desconto
VB = Total do Valor Bruto

*/
BEGIN


SELECT  To_Char(Sum(VL_PRECO_LIQUIDO),'999999990.00') VL_LIQUIDO,
        To_Char(sum(((QT_MATERIAL * VL_UNITARIO_MATERIAL) * pr_desconto) / 100),'999999990.00')  VL_DESCONTO ,
        To_Char(Sum(QT_MATERIAL * VL_UNITARIO_MATERIAL),'999999990.00') VL_BRUTO
INTO STRICT	vl_liquido_w,
	vl_desconto_w,
	vl_total_w
FROM    COT_COMPRA_FORN_ITEM
WHERE   NR_SEQ_COT_FORN = nr_sequencia_p
AND     NR_COT_COMPRA =  nr_cot_compra_p;



if (ie_tipo_p = 'VL') then
	ds_retorno_w := vl_liquido_w;
elsif (ie_tipo_p = 'VD') then
	ds_retorno_w := vl_desconto_w;
elsif (ie_tipo_p = 'VB') then
	ds_retorno_w := vl_total_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
