CREATE OR REPLACE FUNCTION obter_valores_empenho_oc ( nr_ordem_compra_p bigint, nr_empenho_p bigint, cd_conta_contabil_p text, ie_resultado_p text) RETURNS bigint AS $body$
DECLARE


/* ie_resultado_p
S = Soma do valor do empenho sem estorno;
E = Estorno dos itens cancelados por Conta Cont√°bil;
*/
vl_resultado_w		double precision;


BEGIN

if (ie_resultado_p = 'S') then
	begin

	select	sum(vl_unitario_material * qt_material)
	into STRICT	vl_resultado_w
	from	ordem_compra_item
	where	nr_ordem_compra	= nr_ordem_compra_p
	and	nr_empenho = nr_empenho_p;

	end;
elsif (ie_resultado_p = 'E') then
	begin

	select	sum(vl_unitario_material * qt_material)
	into STRICT	vl_resultado_w
	from	ordem_compra_item
	where (nr_ordem_compra = nr_ordem_compra_p)
	and (obter_se_item_oc_cancelado(nr_ordem_compra, nr_item_oci) = 'S')
	and (cd_conta_contabil = cd_conta_contabil_p);

	end;
end if;

return	vl_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
