CREATE OR REPLACE FUNCTION obter_valores_filtro_ge ( ie_opcao_p text, cd_estabelecimento_p setor_atendimento.cd_setor_atendimento%TYPE, nm_usuario_p usuario.nm_usuario%TYPE) RETURNS varchar AS $body$
DECLARE

		
        
cd_setor_atendimento_w	    usuario_setor_v.cd_setor_atendimento%TYPE;
ds_setor_atendimento_w	    varchar(200);
ds_retorno_w		        varchar(4000) := '';		

c01 CURSOR FOR
SELECT 	cd_setor_atendimento cd,
	substr(obter_nome_setor(cd_setor_atendimento),1,60) ds
from   	usuario_setor_v
where 	nm_usuario = nm_usuario_p;

c02 CURSOR FOR
SELECT 	cd_setor_atendimento cd,
	ds_setor_atendimento ds
from   	setor_atendimento
where 	cd_estabelecimento = cd_estabelecimento_p
order by 2;			
			

BEGIN

if (ie_opcao_p = '1') then

	open C01;
	loop
	fetch C01 into	
		cd_setor_atendimento_w,
		ds_setor_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		ds_retorno_w := ds_retorno_w || cd_setor_atendimento_w || ',';
		
		end;
	end loop;
	close C01;
	
else

	open C02;
	loop
	fetch C02 into	
		cd_setor_atendimento_w,
		ds_setor_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		
		ds_retorno_w := ds_retorno_w || cd_setor_atendimento_w || ',';
		
		end;
	end loop;
	close C02;
	
end if;

return ds_retorno_w;

end;
$body$
