CREATE OR REPLACE FUNCTION obter_valores_hidricos (nr_atendimento_p bigint, ie_exige_lib_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_ganho_w	double precision;
qt_perda_w	double precision;
qt_diferenca_w	double precision;
qt_retorno_w	double precision;
/*
G - Ganho
P - Perda
D - DiferenÃ§a
*/
BEGIN
if (coalesce(nr_atendimento_p,0) > 0) then

	select	sum(CASE WHEN a.ie_perda_ganho='G' THEN  c.qt_volume  ELSE 0 END ) qt_ganho,
		sum(CASE WHEN a.ie_perda_ganho='P' THEN  c.qt_volume  ELSE 0 END ) qt_perda,
		sum(CASE WHEN a.ie_perda_ganho='G' THEN  c.qt_volume  ELSE 0 END  - CASE WHEN a.ie_perda_ganho='P' THEN  c.qt_volume  ELSE 0 END ) qt_diferenca
	into STRICT	qt_ganho_w,
		qt_perda_w,
		qt_diferenca_w
	from	tipo_perda_ganho b,
		grupo_perda_ganho a,
		atendimento_perda_ganho c
	where	c.nr_atendimento = nr_atendimento_p
	and	b.nr_sequencia = c.nr_seq_tipo
	and	b.nr_seq_grupo = a.nr_sequencia
	and     coalesce(b.ie_soma_bh,'S') = 'S'
	and     ((ie_exige_lib_p = 'N') or (c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> ''))
	and     coalesce(c.ie_situacao,'A') = 'A';

	if (ie_opcao_p = 'G') then
		qt_retorno_w := qt_ganho_w;
	elsif (ie_opcao_p = 'P') then
		qt_retorno_w := qt_perda_w;
	elsif (ie_opcao_p = 'D') then
		qt_retorno_w := qt_diferenca_w;
	end if;
end if;

return coalesce(qt_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
