CREATE OR REPLACE FUNCTION obter_valores_item_conta ( nr_interno_conta_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_propaci_p bigint, cd_material_p bigint, nr_seq_matpaci_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision	:= 0;
vl_glosa_w		double precision;
/*	ie_opcao_p

G	glosa aceita

*/
BEGIN

select	coalesce(sum(vl_glosa),0)
into STRICT	vl_glosa_w
from (SELECT	a.vl_glosa
	from	motivo_glosa b,
		convenio_retorno_glosa a
	where	coalesce(a.ie_acao_glosa,b.ie_acao_glosa) = 'A'
	and	a.cd_motivo_glosa	= b.cd_motivo_glosa
	and	a.nr_seq_propaci	= nr_seq_propaci_p
	
union all

	SELECT	a.vl_glosa
	from	convenio_retorno_item c,
		motivo_glosa b,
		convenio_retorno_glosa a
	where	c.nr_interno_conta	= nr_interno_conta_p
	and	a.nr_seq_ret_item	= c.nr_sequencia
	and	coalesce(a.ie_acao_glosa,b.ie_acao_glosa) = 'A'
	and	a.cd_motivo_glosa	= b.cd_motivo_glosa
	and	a.ie_origem_proced	= ie_origem_proced_p
	and	a.cd_procedimento	= cd_procedimento_p
	and (a.nr_seq_propaci <> nr_seq_propaci_p or coalesce(a.nr_seq_propaci::text, '') = '')
	
union all

	select	a.vl_glosa
	from	motivo_glosa b,
		convenio_retorno_glosa a
	where	coalesce(a.ie_acao_glosa,b.ie_acao_glosa) = 'A'
	and	a.cd_motivo_glosa	= b.cd_motivo_glosa
	and	a.nr_seq_matpaci	= nr_seq_matpaci_p
	
union all

	select	a.vl_glosa
	from	convenio_retorno_item c,
		motivo_glosa b,
		convenio_retorno_glosa a
	where	c.nr_interno_conta	= nr_interno_conta_p
	and	a.nr_seq_ret_item	= c.nr_sequencia
	and	coalesce(a.ie_acao_glosa,b.ie_acao_glosa) = 'A'
	and	a.cd_motivo_glosa	= b.cd_motivo_glosa
	and	a.cd_material		= cd_material_p
	and (a.nr_seq_matpaci <> nr_seq_matpaci_p or coalesce(a.nr_seq_matpaci::text, '') = '')) alias10;

if (ie_opcao_p	= 'G') then
	vl_retorno_w	:= vl_glosa_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
