CREATE OR REPLACE FUNCTION obter_valores_lote_contest ( nr_seq_lote_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision := 0;

/*IE_OPCAO_P
VC - Valor total contestado
*/
BEGIN
if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then

	if (ie_opcao_p = 'VC') then
		select	sum(a.vl_original) vl_original
		into STRICT	vl_retorno_w
		from	pls_contestacao a
		where	a.nr_seq_lote = nr_seq_lote_p;
	end if;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
