CREATE OR REPLACE FUNCTION obter_valores_nf ( nr_seq_nota_fiscal_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*	OS 78991 - SÃ­rio.

	ie_opcao_p
 '1'	- Valor da mercadoria
*/
vl_retorno_w		double precision;
vl_mercadoria_w		double precision;


BEGIN

if (coalesce(nr_seq_nota_fiscal_p,0)	> 0) then

	select	max(vl_mercadoria)
	into STRICT	vl_mercadoria_w
	from	nota_fiscal
	where	nr_sequencia	= nr_seq_nota_fiscal_p;

	if (ie_opcao_p	= '1') then
		vl_retorno_w	:= vl_mercadoria_w;
	else
		vl_retorno_w	:= 0;
	end if;

end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
