CREATE OR REPLACE FUNCTION obter_valores_notas_gif (cd_estabelecimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_opcao_p text, ie_tipo_gif_p text, cd_cgc_p text, cst_p text, cfps_p text, cnae_p text) RETURNS varchar AS $body$
DECLARE


retorno_w		varchar(15);
nr_sequencia_w		varchar(10);
vl_base_calculo_w	bigint;
tx_tributo_w		bigint;
vl_tributo_w		bigint;
vl_mercadoria_w		bigint;


BEGIN

select	max(vl_base_calculo),
	max(tx_tributo),
	max(vl_tributo)
into STRICT	vl_base_calculo_w,
	tx_tributo_w,
	vl_tributo_w
from (
	SELECT	coalesce(Sum(x.vl_base_calculo),0) vl_base_calculo,
		coalesce(max(x.tx_tributo),0) tx_tributo,
		coalesce(Sum(x.vl_tributo),0) vl_tributo
	from	nota_fiscal_item_trib x,
		nota_fiscal n,
		tributo t,
		situacao_trib_prest_serv s,
		operacao_nota a
	where	x.nr_sequencia = n.nr_sequencia
	and	x.cd_tributo    = t.cd_tributo
	and	x.nr_seq_sit_trib = s.nr_sequencia
	and	a.cd_operacao_nf = n.cd_operacao_nf
	and	t.ie_tipo_tributo = 'ISS'
	and	substr(obter_se_nota_entrada_saida(n.nr_sequencia),1,1) = 'S'
	and	ie_tipo_gif_p = 'SP'
	and	dt_emissao between dt_inicial_p and fim_dia(dt_final_p)
	and	n.ie_situacao = '1'
	and	lpad(substr(s.cd_situacao,1,3),3,'0') = cst_p
	and	obter_cfps_municipio(n.cd_estabelecimento,n.cd_cgc,n.cd_pessoa_fisica,'FLORIANOPOLIS') = cfps_p
	and	Lpad(substr(obter_cnae_florianopolis(n.cd_estabelecimento,n.nr_sequencia),1, 11),11,'00000000000') = cnae_p
	and	n.cd_cgc_emitente = cd_cgc_p
	and	(n.dt_atualizacao_estoque IS NOT NULL AND n.dt_atualizacao_estoque::text <> '')
	and	a.ie_servico = 'S'
	and	n.cd_estabelecimento = cd_estabelecimento_p
	
union all

	SELECT	coalesce(Sum(x.vl_base_calculo),0) vl_base_calculo,
		coalesce(max(x.tx_tributo),0) tx_tributo,
		coalesce(Sum(x.vl_tributo),0) vl_tributo
	from	nota_fiscal_item_trib x,
		nota_fiscal n,
		tributo t,
		situacao_trib_prest_serv s,
		operacao_nota a,
		estabelecimento e
	where	x.nr_sequencia = n.nr_sequencia
	and	x.cd_tributo    = t.cd_tributo
	and	x.nr_seq_sit_trib = s.nr_sequencia
	and	a.cd_operacao_nf = n.cd_operacao_nf
	and	t.ie_tipo_tributo = 'ISS'
	and	substr(obter_se_nota_entrada_saida(n.nr_sequencia),1,1) <> 'S'
	and	ie_tipo_gif_p = 'ST'
	and	dt_emissao between dt_inicial_p and fim_dia(dt_final_p)
	and	n.ie_situacao = '1'
	and	lpad(substr(s.cd_situacao,1,3),3,'0') = cst_p
	and	obter_cfps_municipio(n.cd_estabelecimento,n.cd_cgc,n.cd_pessoa_fisica,'FLORIANOPOLIS') = cfps_p
	and	Lpad(substr(obter_cnae_florianopolis(n.cd_estabelecimento,n.nr_sequencia),1, 11),11,'00000000000') = cnae_p
	and	n.cd_cgc_emitente <> e.cd_cgc
	and	n.cd_estabelecimento = e.cd_estabelecimento
	and	(n.dt_atualizacao_estoque IS NOT NULL AND n.dt_atualizacao_estoque::text <> '')
	and	a.ie_servico = 'S'
	and	n.cd_estabelecimento = cd_estabelecimento_p
	) alias35;

select	coalesce(Sum(z.vl_mercadoria),0)
into STRICT	vl_mercadoria_w
from	nota_fiscal z
where 	exists (	SELECT	1 total
			from	nota_fiscal_item_trib x,
				nota_fiscal n,
				tributo t,
				situacao_trib_prest_serv s,
				operacao_nota a
			where	x.nr_sequencia = n.nr_sequencia
			and	x.cd_tributo    = t.cd_tributo
			and	x.nr_seq_sit_trib = s.nr_sequencia
			and	a.cd_operacao_nf = n.cd_operacao_nf
			and	t.ie_tipo_tributo = 'ISS'
			and	substr(obter_se_nota_entrada_saida(n.nr_sequencia),1,1) = 'S'
			and	ie_tipo_gif_p = 'SP'
			and	dt_emissao between dt_inicial_p and fim_dia(dt_final_p)
			and	n.ie_situacao = '1'
			and	lpad(substr(s.cd_situacao,1,3),3,'0') = cst_p
			and	obter_cfps_municipio(n.cd_estabelecimento,n.cd_cgc,n.cd_pessoa_fisica,'FLORIANOPOLIS') = cfps_p
			and	Lpad(substr(obter_cnae_florianopolis(n.cd_estabelecimento,n.nr_sequencia),1, 11),11,'00000000000') = cnae_p
			and	n.cd_cgc_emitente = cd_cgc_p
			and	(n.dt_atualizacao_estoque IS NOT NULL AND n.dt_atualizacao_estoque::text <> '')
			and	a.ie_servico = 'S'
			and	n.cd_estabelecimento = cd_estabelecimento_p
			and	z.nr_sequencia = n.nr_sequencia
			
union all

			SELECT	1 total
			from	nota_fiscal_item_trib x,
				nota_fiscal n,
				tributo t,
				situacao_trib_prest_serv s,
				operacao_nota a,
				estabelecimento e
			where	x.nr_sequencia = n.nr_sequencia
			and	x.cd_tributo    = t.cd_tributo
			and	x.nr_seq_sit_trib = s.nr_sequencia
			and	a.cd_operacao_nf = n.cd_operacao_nf
			and	t.ie_tipo_tributo = 'ISS'
			and	substr(obter_se_nota_entrada_saida(n.nr_sequencia),1,1) <> 'S'
			and	ie_tipo_gif_p = 'ST'
			and	dt_emissao between dt_inicial_p and fim_dia(dt_final_p)
			and	n.ie_situacao = '1'
			and	lpad(substr(s.cd_situacao,1,3),3,'0') = cst_p
			and	obter_cfps_municipio(n.cd_estabelecimento,n.cd_cgc,n.cd_pessoa_fisica,'FLORIANOPOLIS') = cfps_p
			and	Lpad(substr(obter_cnae_florianopolis(n.cd_estabelecimento,n.nr_sequencia),1, 11),11,'00000000000') = cnae_p
			and	n.cd_cgc_emitente <> e.cd_cgc
			and	n.cd_estabelecimento = e.cd_estabelecimento
			and	(n.dt_atualizacao_estoque IS NOT NULL AND n.dt_atualizacao_estoque::text <> '')
			and	a.ie_servico = 'S'
			and	n.cd_estabelecimento = cd_estabelecimento_p
			and	z.nr_sequencia = n.nr_sequencia);

if (ie_opcao_p = 'VLB') then
	retorno_w:= vl_base_calculo_w;
elsif (ie_opcao_p = 'TXT') then
	retorno_w:= tx_tributo_w;
elsif (ie_opcao_p = 'VLT') then
	retorno_w:= vl_tributo_w;
elsif (ie_opcao_p = 'VLM') then
	retorno_w:= vl_mercadoria_w;
end if;

return  retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
