CREATE OR REPLACE FUNCTION obter_valores_nota_presu ( nr_seq_nota_p bigint, cd_tributo_p text, ie_opcao_p text default 'VT') RETURNS bigint AS $body$
DECLARE


/* Rotina feita especificamente para cálculos do Lucro Presumido
VT - Valor do tributo
VB - Valor da base de cálculo do PIS ou do COFINS
*/
vl_resultado_w	double precision;
qt_registro_w	bigint;


BEGIN

if (ie_opcao_p = 'VT') then

	select 	replace(campo_mascara(coalesce(max(coalesce(vl_tributo,0)),'0'),2),'.','')
	into STRICT   	vl_resultado_w
	from   	nota_fiscal_trib c
	where  	c.nr_sequencia = nr_seq_nota_p
	and    	c.cd_tributo = cd_tributo_p;

elsif (ie_opcao_p = 'VB') then

	select 	count(*)
	into STRICT	qt_registro_w
	from 	nota_fiscal_trib c
	where 	c.nr_sequencia = nr_seq_nota_p
	and		c.cd_tributo = cd_tributo_p;

	if (qt_registro_w > 0) then
		select 	replace(campo_mascara(coalesce(max(c.vl_base_calculo),'0'),2),'.','')
		into STRICT	vl_resultado_w
		from	nota_fiscal_trib c
		where	c.nr_sequencia = nr_seq_nota_p
		and	c.cd_tributo = cd_tributo_p;

	elsif (qt_registro_w = 0) then
		vl_resultado_w := 0;
	end if;

end if;

return vl_resultado_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
