CREATE OR REPLACE FUNCTION obter_valores_prescr_trigger_atx ( nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(4000);

/*
HV =  Horas de Validade
PF =  Código de pessoa fisica
A = Atendimento
E = Estabelecimento
P = Prescritor
SA = Setor atendimento
M = Médico
*/
BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	if (ie_opcao_p = 'HV') then
		select	max(nr_horas_validade)
		into STRICT	ds_retorno_w
		from	prescr_medica
		where	nr_prescricao = nr_prescricao_p;
	elsif (ie_opcao_p = 'PF') then
		select	max(cd_pessoa_fisica)
		into STRICT	ds_retorno_w
		from	prescr_medica
		where	nr_prescricao = nr_prescricao_p;
	elsif (ie_opcao_p = 'A') then
		select	max(nr_atendimento)
		into STRICT	ds_retorno_w
		from	prescr_medica
		where	nr_prescricao = nr_prescricao_p;
	elsif (ie_opcao_p = 'E') then
		select	max(cd_estabelecimento)
		into STRICT	ds_retorno_w
		from	prescr_medica
		where	nr_prescricao = nr_prescricao_p;
	elsif (ie_opcao_p = 'P') then
		select	max(cd_prescritor)
		into STRICT	ds_retorno_w
		from	prescr_medica
		where	nr_prescricao = nr_prescricao_p;
	elsif (ie_opcao_p = 'SA') then
		select	max(cd_setor_atendimento)
		into STRICT	ds_retorno_w
		from	prescr_medica
		where	nr_prescricao = nr_prescricao_p;
	elsif (ie_opcao_p = 'M') then
		select	max(cd_medico)
		into STRICT	ds_retorno_w
		from	prescr_medica
		where	nr_prescricao = nr_prescricao_p;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
