SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valores_propaci (nr_interno_conta_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
vl_medico_w		double precision;
vl_auxiliares_w		double precision;
ie_emite_conta_w	varchar(3);

/*
opção:

M 	- Valor médico
MA 	- Valor médico + participantes
ECH	- Emite conta/Honorario
*/
BEGIN

select	coalesce(sum(a.vl_medico),0),
	sum(Obter_Valor_Participante(a.nr_sequencia)),
	max(IE_EMITE_CONTA)
into STRICT	vl_medico_w,
	vl_auxiliares_w,
	ie_emite_conta_w
from	procedimento_paciente a
where	a.nr_interno_conta	= nr_interno_conta_p
and		coalesce(a.cd_motivo_exc_conta::text, '') = '';

if (ie_opcao_p = 'M') then
	vl_retorno_w := vl_medico_w;
end if;

if (ie_opcao_p = 'ME') then
	vl_retorno_w := vl_medico_w + vl_auxiliares_w;
end if;

if (ie_opcao_p = 'ECH') and
	( (ie_emite_conta_w = '71') or (ie_emite_conta_w = '91') or (ie_emite_conta_w = '92') or (ie_emite_conta_w = '93') ) then
	vl_retorno_w := vl_medico_w;
end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valores_propaci (nr_interno_conta_p bigint, ie_opcao_p text) FROM PUBLIC;

