CREATE OR REPLACE FUNCTION obter_valores_titulo_retorno (nr_titulo_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p
M - Valor a menor
*/
vl_retorno_w	double precision := 0;


BEGIN

select	sum(a.vl_amenor)
into STRICT	vl_retorno_w
from	convenio_retorno_item a
where	a.nr_titulo	= nr_titulo_p;

return	coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
