SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valores_tit_rec (nr_titulo_p bigint, ie_valor_p text) RETURNS bigint AS $body$
DECLARE

 
/* 
V - Valor do titulo 
S - Saldo do titulo 
D - Valor de descontos 
I - Valor dos impostos 
GP - Glosa principal 
GA - Glosa auxiliar 
VG - Valor da glosa 
R - Valor Recebido 
*/
 
 
vl_titulo_w		double precision;
vl_saldo_titulo_w	double precision;
vl_retorno_w		double precision;
vl_glosa_ap_w		double precision;
vl_glosa_w		double precision;
vl_desconto_w		double precision;


BEGIN 
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then 
	select	max(a.vl_titulo), 
		max(a.vl_saldo_titulo) 
	into STRICT	vl_titulo_w, 
		vl_saldo_titulo_w 
	from	titulo_receber a 
	where	a.nr_titulo	= nr_titulo_p;
	 
	if (ie_valor_p	= 'V') then 
		vl_retorno_w	:= vl_titulo_w;
	elsif (ie_valor_p	= 'S') then 
		vl_retorno_w	:= vl_saldo_titulo_w;
	elsif (ie_valor_p	= 'D') then 
		select	coalesce(sum(a.vl_descontos),0) vl_desconto 
		into STRICT	vl_retorno_w 
		from	titulo_receber_liq a 
		where	a.nr_titulo	= nr_titulo_p;
	elsif (ie_valor_p	= 'I') then 
		select	coalesce(sum(a.vl_tributo),0) 
		into STRICT	vl_retorno_w 
		from	titulo_receber_trib a 
		where	a.nr_titulo	= nr_titulo_p;
	elsif (ie_valor_p	= 'GP') then 
		select	coalesce(sum(a.vl_glosa_ato_coop_princ),0) vl_glosa_ap 
		into STRICT	vl_retorno_w 
		from	titulo_receber_liq a 
		where	a.nr_titulo	= nr_titulo_p;
	elsif (ie_valor_p	= 'GA') then 
		select	coalesce(sum(a.vl_glosa_ato_coop_aux),0) vl_glosa 
		into STRICT	vl_retorno_w 
		from	titulo_receber_liq a 
		where	a.nr_titulo	= nr_titulo_p;
	elsif (ie_valor_p	= 'VG') then 
		select	coalesce(sum(a.vl_glosa),0) vl_glosa 
		into STRICT	vl_retorno_w 
		from	titulo_receber_liq a 
		where	a.nr_titulo	= nr_titulo_p;
	elsif (ie_valor_p	= 'R') then 
		select	coalesce(sum(a.vl_recebido),0) vl_recebido 
		into STRICT	vl_retorno_w 
		from	titulo_receber_liq a 
		where	a.nr_titulo	= nr_titulo_p;
	end if;
end if;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valores_tit_rec (nr_titulo_p bigint, ie_valor_p text) FROM PUBLIC;

