CREATE OR REPLACE FUNCTION obter_valor_acum_oc_direto ( dt_mesano_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE

 
nr_ordem_compra_w		bigint;
vl_vencimento_w			double precision;	
vl_total_w			double precision;

c01 CURSOR FOR			 
SELECT	distinct 
	a.nr_ordem_compra 
from	ordem_compra a, 
	ordem_compra_item b, 
	local_estoque c, 
	ordem_compra_venc d 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	a.nr_ordem_compra = d.nr_ordem_compra 
and	b.cd_local_estoque = c.cd_local_estoque 
and	(a.dt_aprovacao IS NOT NULL AND a.dt_aprovacao::text <> '') 
and	c.ie_tipo_local = 8 
and	trunc(d.dt_vencimento,'mm') = trunc(dt_mesano_referencia_p,'mm');


BEGIN 
vl_total_w	:= 0;
 
open C01;
loop 
fetch C01 into	 
	nr_ordem_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	select	coalesce(sum(a.vl_vencimento),0) 
	into STRICT	vl_vencimento_w 
	from	ordem_compra_venc a 
	where	a.nr_ordem_compra = nr_ordem_compra_w 
	and	trunc(a.dt_vencimento,'mm') = trunc(dt_mesano_referencia_p,'mm');
		 
	vl_total_w	:= vl_total_w + vl_vencimento_w;
	 
	end;
end loop;
close C01;
 
return	vl_total_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_acum_oc_direto ( dt_mesano_referencia_p timestamp) FROM PUBLIC;

