CREATE OR REPLACE FUNCTION obter_valor_acum_oc_dir_proj ( cd_conta_contabil_p text, nr_seq_proj_rec_p bigint, cd_estabelecimento_p bigint, nr_seq_tipo_compra_p bigint, nr_seq_mod_compra_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_total_liquido_w		double precision;
			

BEGIN 
 
select	coalesce(sum(b.vl_item_liquido),0) 
into STRICT	vl_total_liquido_w 
from	ordem_compra a, 
	ordem_compra_item b 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	a.cd_estabelecimento = cd_estabelecimento_p 
and	trunc(a.dt_ordem_compra,'yyyy') = trunc(clock_timestamp(),'yyyy') 
and	(a.dt_aprovacao IS NOT NULL AND a.dt_aprovacao::text <> '') 
and	coalesce(a.nr_seq_motivo_cancel::text, '') = '' 
and	coalesce(a.nr_seq_licitacao::text, '') = '' 
and	(b.dt_aprovacao IS NOT NULL AND b.dt_aprovacao::text <> '') 
and	coalesce(b.dt_reprovacao::text, '') = '' 
and	((coalesce(nr_seq_proj_rec_p::text, '') = '') or (b.nr_seq_proj_rec			= nr_seq_proj_rec_p)) 
and	((coalesce(cd_conta_contabil_p::text, '') = '') or (b.cd_conta_contabil		= cd_conta_contabil_p)) 
and	((coalesce(nr_seq_tipo_compra_p::text, '') = '') or (a.nr_seq_tipo_compra		= nr_seq_tipo_compra_p)) 
and	((coalesce(nr_seq_mod_compra_p::text, '') = '') or (a.nr_seq_mod_compra		= nr_seq_mod_compra_p)) 
and exists ( 
	SELECT	1 
	from	ordem_compra_item_entrega x 
	where	x.nr_ordem_compra = b.nr_ordem_compra 
	and	x.nr_item_oci = b.nr_item_oci 
	and	coalesce(x.dt_cancelamento::text, '') = '');
 
return	vl_total_liquido_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
