CREATE OR REPLACE FUNCTION obter_valor_adic_viagem (nr_seq_reserva_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	double precision;


BEGIN

if (nr_seq_reserva_p IS NOT NULL AND nr_seq_reserva_p::text <> '') then

	select	coalesce(sum(vl_reserva),0)
	into STRICT	ds_retorno_w
	from	via_reserva
	where	ie_adicional = 'S'
	and		nr_seq_reserva_princ = nr_seq_reserva_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
