CREATE OR REPLACE FUNCTION obter_valor_agend_coletivo (NR_SEQ_AGENDA_P bigint DEFAULT NULL) RETURNS varchar AS $body$
DECLARE


DS_RETORNO_W	varchar(1) := 'N';


BEGIN

IF (NR_SEQ_AGENDA_P IS NOT NULL AND NR_SEQ_AGENDA_P::text <> '') THEN

	SELECT	coalesce(MAX('S'),'N')
	INTO STRICT	DS_RETORNO_W
	FROM	AGENDA_INTEGRADA
	WHERE	NR_SEQUENCIA = NR_SEQ_AGENDA_P
	AND	IE_AGEND_COLETIVO = 'S'
	AND	coalesce(CD_PESSOA_FISICA::text, '') = '';

END IF;

RETURN	DS_RETORNO_W;

END;
