CREATE OR REPLACE FUNCTION obter_valor_atrib_localizar ( cd_estabelecimento_p bigint, nr_seq_atributo_p bigint, cd_perfil_p bigint, nm_usuario_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


vl_padrao_w		varchar(80);
ie_readonly_w		varchar(1);
ie_tipo_restricao_w	smallint;
ie_obrigatorio_w	varchar(1);
qt_minima_caracter_w	smallint;
ie_grid_w		varchar(1) := 'S';
ie_focus_w		varchar(1) := 'N';

C01 CURSOR FOR
	SELECT	vl_padrao,
		coalesce(ie_readonly, 'N'),
		coalesce(ie_tipo_restricao, 1),
		coalesce(ie_obrigatorio, 'N'),
		qt_minima_caracter,
		coalesce(ie_grid, 'S'),
		coalesce(ie_focus, 'N')
	from	tipo_localizar_atrib_valor
	where	nr_seq_atributo = nr_seq_atributo_p
	and	coalesce(cd_perfil, coalesce(cd_perfil_p,0))		= coalesce(cd_perfil_p,0)
	and	coalesce(nm_usuario_regra, coalesce(nm_usuario_p,'0'))	= coalesce(nm_usuario_p,'0')
	order by coalesce(cd_perfil,0),
		coalesce(nm_usuario_regra,'AAA');

BEGIN

open C01;
loop
fetch C01 into
	vl_padrao_w,
	ie_readonly_w,
	ie_tipo_restricao_w,
	ie_obrigatorio_w,
	qt_minima_caracter_w,
	ie_grid_w,
	ie_focus_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if	((coalesce(vl_padrao_w::text, '') = '') and (coalesce(ie_tipo_restricao_w::text, '') = '')) then
	begin
	select	coalesce(max(vl_padrao),''),
		coalesce(max(ie_tipo_restricao),0)
	into STRICT	vl_padrao_w,
		ie_tipo_restricao_w
	from	tipo_localizar_atributo
	where	nr_sequencia = nr_seq_atributo_p;
	end;
end if;

if (ie_opcao_p = 'P' ) then
	return vl_padrao_w;
elsif (ie_opcao_p = 'R' ) then
	return ie_readonly_w;
elsif (ie_opcao_p = 'RE') then
	return ie_tipo_restricao_w;
elsif (ie_opcao_p = 'O') then
	return ie_obrigatorio_w;
elsif (ie_opcao_p = 'MICA') then
	return qt_minima_caracter_w;
elsif (ie_opcao_p = 'G') then
	return ie_grid_w;
elsif (ie_opcao_p = 'F') then
	return ie_focus_w;
else
	return '';
end if;

end;
$body$
