CREATE OR REPLACE FUNCTION obter_valor_autorizacao ( nr_seq_autor_conv_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
vl_autorizado_proc_w	double precision;
vl_autorizado_mat_w	double precision;


BEGIN

select	sum(coalesce(vl_autorizado,0))
into STRICT	vl_autorizado_proc_w
from	procedimento_autorizado
where	nr_sequencia_autor = nr_seq_autor_conv_p;

select	sum(CASE WHEN ie_reducao_acrescimo='R' THEN (coalesce(vl_unitario,0) * coalesce(qt_solicitada,0)) - ((coalesce(vl_unitario,0) * coalesce(qt_solicitada,0)) * coalesce(pr_adicional/100,0))  ELSE (coalesce(vl_unitario,0) * coalesce(qt_solicitada,0)) + ((coalesce(vl_unitario,0) * coalesce(qt_solicitada,0)) * coalesce(pr_adicional/100,0)) END )
into STRICT	vl_autorizado_mat_w
from	MATERIAL_AUTORIZADO
where	nr_sequencia_autor = nr_seq_autor_conv_p;

vl_retorno_w := (coalesce(vl_autorizado_mat_w,0) + coalesce(vl_autorizado_proc_w,0));

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
