CREATE OR REPLACE FUNCTION obter_valor_aval_prod ( nr_seq_tipo_aval_p bigint, nr_seq_item_aval_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255);
ie_resultado_w	varchar(10);
ds_resultado_w	mat_aval_quest_result.ds_resultado%type;
qt_resultado_w	mat_aval_quest_result.qt_resultado%type;
cd_dominio_w	med_item_avaliar.cd_dominio%type;
ds_tabela_w	varchar(30);
cd_campo_chave_w	varchar(30);
ds_descricao_w	varchar(30);
ds_sql_w	varchar(4000);


BEGIN 
 
select	c.ie_resultado, 
	c.cd_dominio 
into STRICT	ie_resultado_w, 
	cd_dominio_w 
from 	mat_aval_quest a, 
	med_tipo_avaliacao b, 
	med_item_avaliar c 
where	b.nr_sequencia = a.nr_seq_tipo_avaliacao 
and	c.nr_seq_tipo = b.nr_sequencia 
and	a.nr_sequencia = nr_seq_tipo_aval_p 
and	c.nr_sequencia = nr_seq_item_aval_p 
order by c.nr_seq_apresent;
 
select	c.ds_resultado, 
	c.qt_resultado 
into STRICT	ds_resultado_w, 
	qt_resultado_w 
from 	mat_aval_quest a, 
	med_tipo_avaliacao b, 
	mat_aval_quest_result c 
where	b.nr_sequencia = a.nr_seq_tipo_avaliacao 
and	c.nr_seq_quest = a.nr_sequencia 
and	a.nr_sequencia = nr_seq_tipo_aval_p 
and	c.nr_seq_item = nr_seq_item_aval_p;
 
if (ie_resultado_w = 'A') or (ie_resultado_w = 'C') or (ie_resultado_w = 'D') or (ie_resultado_w = 'U') then 
	 
	ds_retorno_w := ds_resultado_w;
	 
elsif (ie_resultado_w = 'B') then	 
	 
	if (qt_resultado_w = 0) then 
		ds_retorno_w := wheb_mensagem_pck.get_texto(306950); -- NÃ£o 
	elsif (qt_resultado_w = 1) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(306949); -- Sim 
	end if;	
		 
elsif (ie_resultado_w = 'S') then 
	 
	select	c.ds_resultado 
	into STRICT	ds_retorno_w 
	from	med_tipo_avaliacao a, 
		med_item_avaliar b, 
		med_item_avaliar_res c 
	where	b.nr_seq_tipo = a.nr_sequencia 
	and	c.nr_seq_item = b.nr_sequencia 
	and	c.nr_seq_item = nr_seq_item_aval_p 
	and	nr_seq_res = qt_resultado_w;
 
elsif (ie_resultado_w = 'V') then 
 
	ds_retorno_w := qt_resultado_w;
	 
elsif (ie_resultado_w = 'O') and (cd_dominio_w IS NOT NULL AND cd_dominio_w::text <> '') then 
 
	select	ds_valor_dominio 
	into STRICT	ds_retorno_w 
	from	med_valor_dominio 
	where	nr_seq_dominio = cd_dominio_w 
	and	vl_dominio = qt_resultado_w;
 
elsif (ie_resultado_w = 'Z') and (ds_resultado_w IS NOT NULL AND ds_resultado_w::text <> '') then 
 
	select	obter_valor_campo_separador(DS_COMPLEMENTO, 7, ';'), 
		obter_valor_campo_separador(DS_COMPLEMENTO, 8, ';'), 
		obter_valor_campo_separador(DS_COMPLEMENTO, 9, ';') 
	into STRICT	ds_tabela_w, 
		cd_campo_chave_w, 
		ds_descricao_w 
	from 	med_item_avaliar 
	where	nr_sequencia = nr_seq_item_aval_P;
	 
	ds_sql_w :=  '	select	'||ds_descricao_w|| 
		   '	from	'||ds_tabela_w|| 
		   '	where	'||cd_campo_chave_w||' = '''||ds_resultado_w||''' ';
	EXECUTE ds_sql_w into STRICT ds_retorno_w;
 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
