CREATE OR REPLACE FUNCTION obter_valor_bordero_js ( nr_seq_conv_receb_p bigint, nr_bordero_rec_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
qt_convenio_w		bigint;
dt_recebimento_w	timestamp;
qt_registro_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	bordero_recebimento a
where	a.nr_bordero   = nr_bordero_rec_p
and	(a.dt_recebimento IS NOT NULL AND a.dt_recebimento::text <> '');

if (qt_registro_w IS NOT NULL AND qt_registro_w::text <> '') then
	begin
	select	max(a.dt_recebimento)
	into STRICT	dt_recebimento_w
	from	bordero_recebimento a
	where	a.nr_bordero   = nr_bordero_rec_p;
	end;
end if;

select	count(*)
into STRICT	qt_convenio_w
from	convenio_ret_receb a
where	a.nr_seq_receb = nr_seq_conv_receb_p;

if (coalesce(dt_recebimento_w::text, '') = '') and (qt_convenio_w = 0) then
	begin
	ds_retorno_w := 'S';
	end;
end if;

return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
