CREATE OR REPLACE FUNCTION obter_valor_bordero_rec (nr_bordero_p bigint) RETURNS bigint AS $body$
DECLARE


vl_bordero_w	double precision;


BEGIN

select	coalesce(sum(vl_abaixar),0)
into STRICT	vl_bordero_w
from	bordero_tit_rec
where	nr_bordero	= nr_bordero_p;

return vl_bordero_w;

end;
