CREATE OR REPLACE FUNCTION obter_valor_brasindice2 ( cd_laboratorio_p text, cd_medicamento_p text, cd_apresentacao_p text, qt_conversao_p bigint, cd_material_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* ie_opcao_p:	'C' - Convertido */

vl_preco_medicamento_w		double precision;	
cd_estabelecimento_w		bigint;


BEGIN

begin

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

select	max(coalesce(vl_preco_medicamento * (1 + coalesce(pr_ipi,0)/100),0))
into STRICT	vl_preco_medicamento_w
from	brasindice_preco a
where	a.cd_laboratorio 						= cd_laboratorio_p 
and 	a.cd_medicamento 						= cd_medicamento_p
and 	a.cd_apresentacao 						= cd_apresentacao_p
and 	coalesce(a.cd_estabelecimento, coalesce(cd_estabelecimento_w, 0)) 	= coalesce(cd_estabelecimento_w, 0)
and 	a.dt_inicio_vigencia 						= (	SELECT 	max(b.dt_inicio_vigencia) 
										from	brasindice_preco b 
										where	b.cd_laboratorio 	= cd_laboratorio_p 
										and	b.cd_medicamento 	= cd_medicamento_p
										and 	b.cd_apresentacao 	= cd_apresentacao_p
										and 	coalesce(b.cd_estabelecimento, coalesce(cd_estabelecimento_w, 0)) 	= coalesce(cd_estabelecimento_w, 0)
										);
exception
	when no_data_found then
		vl_preco_medicamento_w := 0;
end;

if	(qt_conversao_p > 0  AND ie_opcao_p = 'C') then
	vl_preco_medicamento_w := vl_preco_medicamento_w / qt_conversao_p;
end if;
/*
if	(vl_preco_medicamento_w = 0) then
	select	nvl(max(vl_preco_venda),0)
	into	vl_preco_medicamento_w	
	from	preco_material
	where	dt_inicio_vigencia	=	(select	max(dt_inicio_vigencia)
					from	preco_material
					where	cd_material	=	cd_material_p)
	and	cd_material	=	cd_material_p;
end if;	
*/
return	vl_preco_medicamento_w;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
