SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_campo_dep_proto ( cd_protocolo_p bigint, cd_departamento_p bigint, nm_campo_p text) RETURNS varchar AS $body$
DECLARE


  retornar_valor_campo_w varchar(30);


BEGIN

  IF (nm_campo_p = 'IE_FORMA_CALCULO')THEN
    SELECT MAX(ie_forma_calculo)
    INTO STRICT retornar_valor_campo_w
    FROM departamento_protocolo
    WHERE (coalesce(cd_departamento_p::text, '') = '' OR cd_departamento        = cd_departamento_p)
    AND nr_seq_protocol       = cd_protocolo_p;
  END IF;

IF (nm_campo_p = 'QT_DIAS_CALCULO')THEN
  SELECT MAX(qt_dias_calculo)
  INTO STRICT retornar_valor_campo_w
  FROM departamento_protocolo
  WHERE (coalesce(cd_departamento_p::text, '') = '' OR cd_departamento        = cd_departamento_p)
  AND nr_seq_protocol       = cd_protocolo_p;
END IF;

RETURN retornar_valor_campo_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
