CREATE OR REPLACE FUNCTION obter_valor_campo_texto (ds_texto_p text, ds_campo_p text, ds_prox_campo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);


BEGIN

	if ( coalesce(ds_texto_p::text, '') = '' or ds_texto_p = '' ) then

		ds_retorno_w := '';

	elsif ( coalesce(ds_campo_p::text, '') = '' or ds_campo_p = '' ) then

		ds_retorno_w := '';

	elsif ( coalesce(ds_prox_campo_p::text, '') = '' or ds_prox_campo_p = '' ) then

		ds_retorno_w := '';

	else

		begin
			select	substr(ds_retorno, 0, position(upper(ds_prox_campo_p) in upper(ds_retorno)) - 1)
			into STRICT	ds_retorno_w
			from	(SELECT	substr(ds_texto_p, (position(upper(ds_campo_p) in upper(ds_texto_p)) + length(ds_campo_p))) ds_retorno
				) alias10;
		end;

	end if;

	return coalesce(ds_retorno_w,'');

end;
$body$
