CREATE OR REPLACE FUNCTION obter_valor_carga_antiga (cd_laboratorio_p text, cd_medicamento_p text, cd_apresentacao_p text, ie_versao_anterior_p bigint) RETURNS bigint AS $body$
DECLARE


vl_preco_w	double precision;
dt_vigencia_w	timestamp;


BEGIN

select	min(dt_inicio_vigencia)
into STRICT	dt_vigencia_w
from	brasindice_preco
where	ie_versao_atual	= ie_versao_anterior_p;

select	coalesce(max(vl_preco_medicamento),0)
into STRICT	vl_preco_w
from	brasindice_preco
where	cd_medicamento		= cd_medicamento_p
and	cd_laboratorio		= cd_laboratorio_p
and	cd_apresentacao		= cd_apresentacao_p
and	ie_versao_atual		= ie_versao_anterior_p
and	dt_inicio_vigencia		= dt_vigencia_w;

return	vl_preco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
