CREATE OR REPLACE FUNCTION obter_valor_carta ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_registros_w	integer;
vl_retorno_w	double precision;

c01 CURSOR FOR
SELECT	vl_autorizado
from	autorizacao_convenio_compl
where	nr_sequencia_autor = nr_sequencia_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	coalesce(dt_cancelamento::text, '') = ''
and	ie_tipo = 'S'
order by	nr_sequencia;


BEGIN

vl_retorno_w	:= 0;

select	count(*)
into STRICT	qt_registros_w
from	autorizacao_convenio_compl
where	nr_sequencia_autor = nr_sequencia_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	coalesce(dt_cancelamento::text, '') = ''
and	ie_tipo = 'S';

if (qt_registros_w = 0) then
	begin
	select	sum(vl_autorizado)
	into STRICT	vl_retorno_w
	from	autorizacao_convenio_compl
	where	nr_sequencia_autor = nr_sequencia_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	coalesce(dt_cancelamento::text, '') = ''
	and	ie_tipo = 'C';
	end;
else
	begin
	open c01;
	loop
	fetch	c01
	into	vl_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		vl_retorno_w := vl_retorno_w;
	end loop;
	close c01;
	end;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
