CREATE OR REPLACE FUNCTION obter_valor_cheques_bordero (nr_bordero_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_sequencia_w		cheque_v.nr_sequencia%type;
nr_cheque_w			cheque_v.nr_cheque%type;
vl_cheque_w			cheque_v.vl_cheque%type;

vl_retorno_w		cheque_v.vl_cheque%type;				
				 
C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
			a.nr_cheque, 
			a.vl_cheque 
	from	agencia_bancaria c, 
			cheque_v a, 
			cheque_bordero_titulo b 
	where	b.nr_bordero      = nr_bordero_p 
	and		b.cd_banco       = a.cd_banco 
	and		b.cd_agencia_bancaria  = a.cd_agencia_bancaria 
	and		b.nr_seq_cheque     = a.nr_sequencia 
	and		a.cd_banco       = c.cd_banco 
	and		a.cd_agencia_bancaria  = c.cd_agencia_bancaria 
	and		coalesce(a.dt_cancelamento::text, '') = '' 
	group by	a.nr_cheque, 
			a.vl_cheque, 
			a.nr_sequencia 
	order by	a.nr_cheque;
								

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w, 
	nr_cheque_w, 
	vl_cheque_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
		vl_retorno_w := coalesce(vl_retorno_w,0) + coalesce(vl_cheque_w,0);
	 
	end;
end loop;
close C01;
 
return	coalesce(vl_retorno_w,0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_cheques_bordero (nr_bordero_p bigint) FROM PUBLIC;

