CREATE OR REPLACE FUNCTION obter_valor_classif_tit_pagar (nr_titulo_p bigint, nr_seq_classif_p bigint, vl_referencia_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_retorno_w		double precision;
vl_classificacao_w		double precision;
vl_titulo_original_w		double precision;
vl_titulo_w		double precision;


BEGIN 
 
if (coalesce(nr_seq_classif_p, 0) = 0) then 
	vl_retorno_w		:= vl_referencia_p;
 
else 
	select	vl_titulo 
	into STRICT	vl_classificacao_w 
	from	titulo_pagar_classif 
	where	nr_titulo			= nr_titulo_p 
	and	nr_sequencia		= nr_seq_classif_p;
 
	vl_titulo_original_w		:= obter_dados_tit_pagar(nr_titulo_p, 'V');
	vl_titulo_w		:= obter_dados_tit_pagar(nr_titulo_p, 'V');
 
	vl_classificacao_w		:= vl_classificacao_w * dividir_sem_round(vl_titulo_w, vl_titulo_original_w);
	vl_retorno_w		:= vl_classificacao_w * dividir_sem_round(vl_referencia_p, vl_titulo_w);
 
end if;
 
return	vl_retorno_w;
	 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
