SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_composicao ( nr_seq_composicao_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar o valor total dos procedimentos e materiais da composição dos pacotes.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

'P' - PROCEDIMENTO
'M' - MATERIAL
'T' - TOTAL
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
vl_proc_composicao_w	double precision;
vl_mat_composicao_w	double precision;
ds_retorno_w		double precision;


BEGIN

select	coalesce(sum(vl_negociado),0)
into STRICT	vl_proc_composicao_w
from	pls_pacote_procedimento
where	nr_seq_composicao = nr_seq_composicao_p
and	ie_situacao = 'A';

select	coalesce(sum(vl_negociado),0)
into STRICT	vl_mat_composicao_w
from	pls_pacote_material
where	nr_seq_composicao = nr_seq_composicao_p
and	ie_situacao = 'A';

if (ie_opcao_p = 'P') then
	ds_retorno_w := vl_proc_composicao_w;

elsif (ie_opcao_p = 'M') then
	ds_retorno_w := vl_mat_composicao_w;

elsif (ie_opcao_p = 'T') then
	ds_retorno_w := (vl_mat_composicao_w + vl_proc_composicao_w);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_composicao ( nr_seq_composicao_p bigint, ie_opcao_p text) FROM PUBLIC;

