CREATE OR REPLACE FUNCTION obter_valor_conpaci_repasse (nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE


vl_repasse_mat_w		double precision;
vl_repasse_proc_w		double precision;
vl_repasse_w			double precision;


BEGIN

select	coalesce(sum(a.vl_repasse_calc),0)
into STRICT	vl_repasse_proc_w
from	procedimento_paciente a
where	a.nr_interno_conta	= nr_interno_conta_p;

select	coalesce(sum(a.vl_repasse_calc),0)
into STRICT	vl_repasse_mat_w
from	material_atend_paciente a
where	a.nr_interno_conta	= nr_interno_conta_p;

vl_repasse_w		:= vl_repasse_proc_w + vl_repasse_mat_w;

return	vl_repasse_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
