CREATE OR REPLACE FUNCTION obter_valor_conpaci_retorno ( nr_interno_conta_p bigint, cd_autorizacao_p text, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE


/* ie_opcao_p
	1. vl recebido
	2. vl glosa indevida
*/
vl_retorno_w	double precision;


BEGIN

vl_retorno_w	:= 0;

if (ie_opcao_p = 1) then

	select	SUM(coalesce(a.vl_recebido,0))
	into STRICT	vl_retorno_w
	from	conta_paciente_retorno a
	where	a.cd_autorizacao	   	= cd_autorizacao_p
	and	a.nr_interno_conta	= nr_interno_conta_p;

end if;

if (ie_opcao_p = 2) then

	select	SUM(coalesce(a.vl_glosa_indevida,0))
	into STRICT	vl_retorno_w
	from	conta_paciente_retorno a
	where	a.cd_autorizacao	   	= cd_autorizacao_p
	and	a.nr_interno_conta	= nr_interno_conta_p;

end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
