CREATE OR REPLACE FUNCTION obter_valor_conpaci_ret_hist (nr_sequencia_p bigint, ie_acao_p bigint, ie_situacao_p text, dt_mes_historico_p timestamp, ie_mes_tudo_p text) RETURNS bigint AS $body$
DECLARE

/*
 0 - Não Auditado
 1 - Recebimento
 2 - Reapresentação
 3 - Glosa Devida
 4 - Glosa Indevida
 5 - Adequação a menor
 6 - Adequação a maior
*/
dt_adequacao_w	timestamp;
vl_retorno_w		double precision;


BEGIN

select coalesce(dt_adequacao,clock_timestamp())
into STRICT dt_adequacao_w
from conta_paciente_retorno
where nr_sequencia = nr_sequencia_p;

select coalesce(sum(vl_historico),0)
into STRICT vl_retorno_w
from hist_audit_conta_paciente b,
 conta_paciente_ret_hist a
where a.nr_seq_hist_audit = b.nr_sequencia
  and a.nr_seq_conpaci_ret = nr_sequencia_p
  and b.ie_acao  = ie_acao_p
  and ((ie_situacao_p = 'P' AND dt_historico < dt_adequacao_w)
	or (ie_situacao_p = 'A' AND dt_historico >= dt_adequacao_w) )
  and ((coalesce(dt_mes_historico_p::text, '') = '') or (ie_mes_tudo_p = 'T') or (a.dt_historico between trunc(dt_mes_historico_p,'month') and last_day(dt_mes_historico_p)));
return vl_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_conpaci_ret_hist (nr_sequencia_p bigint, ie_acao_p bigint, ie_situacao_p text, dt_mes_historico_p timestamp, ie_mes_tudo_p text) FROM PUBLIC;

