CREATE OR REPLACE FUNCTION obter_valor_conta_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
vl_retorno_w		varchar(10);
nr_interno_conta_w	bigint;

 
C01 CURSOR FOR 
SELECT 	nr_interno_conta 
from 	conta_paciente 
where	nr_atendimento = nr_atendimento_p;
	

BEGIN 
if (coalesce(nr_atendimento_p,0) > 0)	then	 
	open C01;
	loop 
	fetch C01 into	 
		nr_interno_conta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		if (coalesce(nr_interno_conta_w,0) > 0) then 
			vl_retorno_w		:= (coalesce(vl_retorno_w,0) + obter_valor_conta(nr_interno_conta_w,0));
		end if;
		end;
	end loop;
	close C01;
end if;
 
return	round((vl_retorno_w)::numeric,2);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
