CREATE OR REPLACE FUNCTION obter_valor_conta_autor ( nr_seq_agenda_p bigint, nr_atendimento_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_valor_conta_w	varchar(1);


BEGIN

if (nr_seq_agenda_p > 0) then

	select	coalesce(max(b.ie_valor_conta),'S')
	into STRICT	ie_valor_conta_w
	from	autorizacao_cirurgia a,
		material_autor_cirurgia b
	where	b.nr_seq_autorizacao	= a.nr_sequencia
	and	a.nr_seq_agenda		=  nr_seq_agenda_p
	and	b.cd_material		= cd_material_p;

end if;
return	ie_valor_conta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
