vl_retorno_w	procedimento_participante.vl_Conta%type:=0;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	coalesce(sum(coalesce(p.vl_Conta,0)),0)
