CREATE OR REPLACE FUNCTION obter_valor_conta_sus (nr_interno_conta_p bigint, ie_tipo_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

				 
vl_retorno_w			double precision := 0;
nr_seq_proc_interno_w		bigint;
cd_procedimento_sus_w		bigint;
ie_origem_proced_sus_w		bigint := 7;
cd_estabelecimento_w		integer;
dt_procedimento_w		timestamp;
cd_convenio_w			integer;
cd_categoria_w			varchar(10);
vl_item_sus_w			double precision;

c01 CURSOR FOR 
	SELECT 	nr_seq_proc_interno, 
		cd_convenio, 
		cd_categoria, 
		dt_procedimento 
	from	procedimento_paciente 
	where	nr_interno_conta = nr_interno_conta_p 
	and	coalesce(cd_motivo_exc_conta::text, '') = '' 
	order by 1;


BEGIN 
 
select	max(cd_estabelecimento) 
into STRICT	cd_estabelecimento_w 
from	conta_paciente 
where	nr_interno_conta = nr_interno_conta_p;
 
open c01;
loop 
fetch c01 into 
	nr_seq_proc_interno_w, 
	cd_convenio_w, 
	cd_categoria_w, 
	dt_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	SELECT * FROM obter_procedimento_tab_interna(nr_seq_proc_interno_w, cd_convenio_w, cd_estabelecimento_w, dt_procedimento_w, cd_procedimento_sus_w, ie_origem_proced_sus_w) INTO STRICT cd_procedimento_sus_w, ie_origem_proced_sus_w;
	 
	if (ie_tipo_atendimento_p <> 1) then 
		begin 
		 
		select	coalesce(sus_obter_preco_proced(cd_estabelecimento_w,dt_procedimento_w,ie_tipo_atendimento_p,cd_procedimento_sus_w,ie_origem_proced_sus_w,2),0) 
		into STRICT	vl_item_sus_w 
		;
		 
		end;
	else 
		begin 
		 
		select	coalesce(sus_obter_preco_proced(cd_estabelecimento_w,dt_procedimento_w,ie_tipo_atendimento_p,cd_procedimento_sus_w,ie_origem_proced_sus_w,1),0) 
		into STRICT	vl_item_sus_w 
		;
		 
		end;
	end if;
	 
	vl_retorno_w := vl_retorno_w + vl_item_sus_w;
	 
	end;
end loop;
close c01;
 
return coalesce(vl_retorno_w,0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
