CREATE OR REPLACE FUNCTION obter_valor_conv_ret_glosa (nr_seq_procedimento_p bigint, nr_seq_material_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p

'G'	= Glosa
'A'	= Adicional
'GG'	= Glosa aceita
'M'	= Amenor

*/
vl_retorno_w	double precision	:= 0;


BEGIN

if (ie_opcao_p = 'A') then
	if (coalesce(nr_seq_procedimento_p,0) > 0) then
		select	coalesce(sum(a.VL_AMAIOR),0)
		into STRICT	vl_retorno_w
		from	convenio_retorno_glosa a
		where	NR_SEQ_PROPACI	= nr_seq_procedimento_p;
	else
		select	coalesce(sum(a.VL_AMAIOR),0)
		into STRICT	vl_retorno_w
		from	convenio_retorno_glosa a
		where	NR_SEQ_MATPACI	= nr_seq_material_p;
	end if;
elsif (ie_opcao_p = 'G') then
	if (coalesce(nr_seq_procedimento_p,0) > 0) then
		select	coalesce(sum(a.VL_GLOSA),0)
		into STRICT	vl_retorno_w
		from	convenio_retorno_glosa a
		where	NR_SEQ_PROPACI	= nr_seq_procedimento_p;
	else
		select	coalesce(sum(a.VL_GLOSA),0)
		into STRICT	vl_retorno_w
		from	convenio_retorno_glosa a
		where	NR_SEQ_MATPACI	= nr_seq_material_p;
	end if;
elsif (ie_opcao_p = 'GG') then
	if (coalesce(nr_seq_procedimento_p,0) > 0) then
		select	coalesce(sum(a.VL_GLOSA),0)
		into STRICT	vl_retorno_w
		from	motivo_glosa b,
			convenio_retorno_glosa a
		where	a.NR_SEQ_PROPACI	= nr_seq_procedimento_p
		and	b.cd_motivo_glosa		= a.cd_motivo_glosa
		and	coalesce(a.ie_acao_glosa,b.ie_acao_glosa) = 'A';
	else
		select	coalesce(sum(a.VL_GLOSA),0)
		into STRICT	vl_retorno_w
		from	motivo_glosa b,
			convenio_retorno_glosa a
		where	NR_SEQ_MATPACI	= nr_seq_material_p
		and	b.cd_motivo_glosa	= a.cd_motivo_glosa
		and	coalesce(a.ie_acao_glosa,b.ie_acao_glosa) = 'A';
	end if;
elsif (ie_opcao_p = 'M') then
	if (coalesce(nr_seq_procedimento_p,0) > 0) then
		select	coalesce(sum(a.VL_GLOSA),0)
		into STRICT	vl_retorno_w
		from	motivo_glosa b,
			convenio_retorno_glosa a
		where	a.NR_SEQ_PROPACI	= nr_seq_procedimento_p
		and	b.cd_motivo_glosa		= a.cd_motivo_glosa
		and	coalesce(a.ie_acao_glosa,b.ie_acao_glosa) = 'R';
	else
		select	coalesce(sum(a.VL_GLOSA),0)
		into STRICT	vl_retorno_w
		from	motivo_glosa b,
			convenio_retorno_glosa a
		where	NR_SEQ_MATPACI	= nr_seq_material_p
		and	b.cd_motivo_glosa	= a.cd_motivo_glosa
		and	coalesce(a.ie_acao_glosa,b.ie_acao_glosa) = 'R';
	end if;
end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
