SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_conv_ret_item ( nr_seq_retorno_item_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

 
/* 
ie_opcao_p: 
'VT'	- Valor da guia 
'VP'	- Valor pago 
'VG'	- Valor glosado 
'VS'	- Valor saldo 
'VA'	- Valor adicional 
'VD'	- Valor desconto 
'VPER'	- Valor perdas 
'A'	- Valor variação ativa 
'P'	- Valor variação passiva 
*/
 
		 
nr_titulo_w		bigint;
cd_estabelecimento_w	smallint;
vl_guia_w		double precision;
vl_retorno_w		double precision;
nr_interno_conta_w	bigint;
cd_autorizacao_w		varchar(20);
vl_pago_w		double precision;
vl_glosado_w		double precision;
vl_glosado_post_w		double precision;
vl_adicional_w		double precision;
vl_perdas_w		double precision;
vl_desconto_w		double precision;
vl_variacao_w		double precision;


BEGIN 
 
select	a.cd_estabelecimento, 
	b.nr_titulo, 
	b.nr_interno_conta, 
	b.cd_autorizacao, 
	coalesce(b.vl_pago,0), 
	coalesce(b.vl_glosado,0), 
	coalesce(b.vl_glosado_post,0), 
	coalesce(b.vl_adicional,0), 
	coalesce(b.vl_perdas,0), 
	coalesce(b.vl_desconto,0) 
into STRICT	cd_estabelecimento_w, 
	nr_titulo_w, 
	nr_interno_conta_w, 
	cd_autorizacao_w, 
	vl_pago_w, 
	vl_glosado_w, 
	vl_glosado_post_w, 
	vl_adicional_w, 
	vl_perdas_w, 
	vl_desconto_w 
from	convenio_retorno a, 
	convenio_retorno_item b 
where	b.nr_sequencia	= nr_seq_retorno_item_p 
and	b.nr_seq_retorno	= a.nr_sequencia;
 
if (ie_opcao_p	= 'VT') or (ie_opcao_p	= 'VS') then 
 
	if (nr_interno_conta_w IS NOT NULL AND nr_interno_conta_w::text <> '') then 
 
		begin 
		select	coalesce(max(b.vl_guia),0) 
		into STRICT	vl_guia_w 
		from	conta_paciente_guia b, 
			conta_paciente a 
		where	a.nr_interno_conta		= nr_interno_conta_w 
		and	a.cd_estabelecimento	= cd_estabelecimento_w 
		and	a.nr_interno_conta		= b.nr_interno_conta 
		and	b.cd_autorizacao		= coalesce(cd_autorizacao_w,b.cd_autorizacao);		
		exception 
		when others then 
			vl_guia_w := 0;
		end;
 
	else 
		begin 
		select	coalesce(a.vl_titulo,0) 
		into STRICT	vl_guia_w 
		from	titulo_receber a 
		where	a.nr_titulo	= nr_titulo_w;		
		exception 
		when others then 
			vl_guia_w := 0;
		end;
	end if;
 
	if (ie_opcao_p	= 'VT') then 
		vl_retorno_w	:= vl_guia_w;
	else 
		vl_retorno_w	:= vl_guia_w - vl_pago_w - vl_glosado_w - vl_glosado_post_w - vl_desconto_w - vl_perdas_w;
	end if;
 
elsif (ie_opcao_p	= 'VP') then 
	vl_retorno_w	:= vl_pago_w;
elsif (ie_opcao_p	= 'VG') then 
	vl_retorno_w	:= vl_glosado_w + vl_glosado_post_w;
elsif (ie_opcao_p	= 'VA') then 
	vl_retorno_w	:= vl_adicional_w;
elsif (ie_opcao_p	= 'VD') then 
	vl_retorno_w	:= vl_desconto_w;
elsif (ie_opcao_p	= 'VPER') then 
	vl_retorno_w	:= vl_perdas_w;
elsif (ie_opcao_p	= 'VV') then 
	vl_retorno_w	:= vl_pago_w + vl_adicional_w;
 
elsif (ie_opcao_p	= 'P') or (ie_opcao_p	= 'A') then 
 
	select	sum(coalesce(a.vl_adicional,0)) 
	into STRICT	vl_variacao_w 
	from	transacao_financeira b, 
		convenio_ret_item_valor a 
	where	a.nr_seq_ret_item		= nr_seq_retorno_item_p 
	and	a.nr_seq_trans_financ	= b.nr_sequencia 
	and	b.ie_variacao_cambial	= ie_opcao_p;
 
	vl_retorno_w	:= vl_variacao_w;
 
end if;
 
return	coalesce(vl_retorno_w,0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_conv_ret_item ( nr_seq_retorno_item_p bigint, ie_opcao_p text) FROM PUBLIC;

