CREATE OR REPLACE FUNCTION obter_valor_copart_wint ( nr_prescricao_p prescr_procedimento.nr_prescricao%type default null, nr_seq_proced_p prescr_procedimento.nr_sequencia%type default null, nr_seq_proc_autor_p procedimento_guia_wint.nr_seq_proc_autor%type default null) RETURNS bigint AS $body$
DECLARE


vl_coparticipacao_w prescr_proced_inf_adic.vl_coparticipacao%type;

BEGIN

if (coalesce(nr_prescricao_p,0) > 0) and (coalesce(nr_seq_proced_p,0) > 0) then

	select	coalesce(max(a.vl_coparticipacao),0)
	into STRICT	vl_coparticipacao_w
	from	prescr_proced_inf_adic a
	where	a.nr_sequencia = (SELECT max(b.nr_sequencia)	
				from 	prescr_proced_inf_adic b
				where 	b.nr_prescricao		= nr_prescricao_p
				and	b.nr_seq_prescricao	= nr_seq_proced_p
				and	b.ie_situacao = 'A');
	
end if;

if (coalesce(nr_seq_proc_autor_p, 0) > 0) then
begin
select  a.vl_coparticipacao
into STRICT    vl_coparticipacao_w
from    procedimento_guia_wint a
where   a.nr_seq_proc_autor = nr_seq_proc_autor_p;
exception
        when no_data_found then
                vl_coparticipacao_w := null;
        when too_many_rows then
                vl_coparticipacao_w := null;
end;

end if;

return	vl_coparticipacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
