CREATE OR REPLACE FUNCTION obter_valor_cotacao_moeda_data ( cd_moeda_p bigint, dt_vigencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_cotacao_w		cotacao_moeda.vl_cotacao%type	:= 0;


BEGIN

begin
	select	coalesce(vl_cotacao,1)
	into STRICT	vl_cotacao_w
	from	cotacao_moeda	b
	where	b.cd_moeda  	= cd_moeda_p
	and 	b.dt_cotacao	= (	SELECT	max(dt_cotacao)
					from 	cotacao_moeda
					where 	cd_moeda 	= cd_moeda_p
					and 	dt_cotacao	<= dt_vigencia_p);
exception
when others then
	vl_cotacao_w := 0;
end;


return	vl_cotacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
