SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_darf (nr_seq_darf_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_titulo_w	double precision;
vl_debito_w	double precision;


BEGIN 
 
select	sum(coalesce((SUBSTR(obter_dados_tit_pagar(a.nr_titulo,'V'),1,200))::numeric ,0)) 
into STRICT	vl_titulo_w 
from	darf_titulo_pagar a, 
	titulo_pagar b 
where	a.nr_titulo	= b.nr_titulo 
and	nr_seq_darf	= nr_seq_darf_p;
 
select	CASE WHEN c.vl_compensado_debito='' THEN  0  ELSE c.vl_compensado_debito END  
into STRICT	vl_debito_w 
from	darf c 
WHERE	c.nr_sequencia	= nr_seq_darf_p;
 
vl_titulo_w	:= vl_titulo_w - vl_debito_w;
 
return	vl_titulo_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_darf (nr_seq_darf_p bigint) FROM PUBLIC;

