SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_deducao_conta ( nr_seq_deducao_conv_p bigint, ie_tipo_calculo_p text, ie_imposto_p text) RETURNS bigint AS $body$
DECLARE


vl_deducao_w	double precision;


BEGIN

if (ie_imposto_p = 'S') then

	select	coalesce(sum(coalesce(vl,0)),0)
	into STRICT	vl_deducao_w
	from (SELECT	coalesce(a.vl_rateio,0) vl
		from	conta_pac_ded_conv_item a,
			conta_pac_deducao_conv b
		where	a.nr_seq_deducao_conv = b.nr_sequencia
		and	a.nr_seq_deducao_conv = nr_seq_deducao_conv_p
		and	b.ie_tipo_calculo = coalesce(ie_tipo_calculo_p, b.ie_tipo_calculo)
		and	(a.nr_seq_propaci_origem IS NOT NULL AND a.nr_seq_propaci_origem::text <> '')
		and	exists (	select	1
				from	propaci_imposto y
				where	y.nr_seq_propaci = a.nr_seq_propaci_origem
				and	y.pr_imposto > 0)
		
union all

		SELECT	coalesce(a.vl_rateio,0) vl
		from	conta_pac_ded_conv_item a,
			conta_pac_deducao_conv b
		where	a.nr_seq_deducao_conv = b.nr_sequencia
		and	a.nr_seq_deducao_conv = nr_seq_deducao_conv_p
		and	b.ie_tipo_calculo = coalesce(ie_tipo_calculo_p, b.ie_tipo_calculo)
		and	(a.nr_seq_matpaci_origem IS NOT NULL AND a.nr_seq_matpaci_origem::text <> '')
		and	exists (	select	1
				from	matpaci_imposto x
				where	x.nr_seq_matpaci = a.nr_seq_matpaci_origem
				and	x.pr_imposto > 0)) alias12;

elsif (ie_imposto_p = 'N') then

	select	coalesce(sum(coalesce(vl,0)),0)
	into STRICT	vl_deducao_w
	from (SELECT	coalesce(a.vl_rateio,0) vl
		from	conta_pac_ded_conv_item a,
			conta_pac_deducao_conv b
		where	a.nr_seq_deducao_conv = b.nr_sequencia
		and	a.nr_seq_deducao_conv = nr_seq_deducao_conv_p
		and	b.ie_tipo_calculo = coalesce(ie_tipo_calculo_p, b.ie_tipo_calculo)
		and	(a.nr_seq_propaci_origem IS NOT NULL AND a.nr_seq_propaci_origem::text <> '')
		and	not exists (	select	1
				from	propaci_imposto y
				where	y.nr_seq_propaci = a.nr_seq_propaci_origem
				and	y.pr_imposto > 0)
		
union all

		SELECT	coalesce(a.vl_rateio,0) vl
		from	conta_pac_ded_conv_item a,
			conta_pac_deducao_conv b
		where	a.nr_seq_deducao_conv = b.nr_sequencia
		and	a.nr_seq_deducao_conv = nr_seq_deducao_conv_p
		and	b.ie_tipo_calculo = coalesce(ie_tipo_calculo_p, b.ie_tipo_calculo)
		and	(a.nr_seq_matpaci_origem IS NOT NULL AND a.nr_seq_matpaci_origem::text <> '')
		and	not exists (	select	1
				from	matpaci_imposto x
				where	x.nr_seq_matpaci = a.nr_seq_matpaci_origem
				and	x.pr_imposto > 0)) alias12;

else

	select	coalesce(sum(coalesce(a.vl_rateio,0)),0) vl
	into STRICT	vl_deducao_w
	from	conta_pac_ded_conv_item a,
		conta_pac_deducao_conv b
	where	a.nr_seq_deducao_conv = b.nr_sequencia
	and	a.nr_seq_deducao_conv = nr_seq_deducao_conv_p
	and	b.ie_tipo_calculo = coalesce(ie_tipo_calculo_p, b.ie_tipo_calculo);

end if;

return vl_deducao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
