SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_desc_titulo ( nr_titulo_p bigint, ds_taxa_desc_p bigint, vl_desconto_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_saldo_titulo_w	titulo_receber.vl_saldo_titulo%type;
vl_retorno_calc_w	double precision	:= 0;


BEGIN

select	max(vl_saldo_titulo)
into STRICT	vl_saldo_titulo_w
from	titulo_receber
where	nr_titulo	= nr_titulo_p;

if (coalesce(vl_saldo_titulo_w,0) > 0) then

	if (ie_opcao_p = 'TX') then

		vl_retorno_calc_w		:= (coalesce(vl_saldo_titulo_w,0) * dividir_sem_round((ds_taxa_desc_p)::numeric,100));

	elsif (ie_opcao_p = 'DS') then

		vl_retorno_calc_w		:= dividir_sem_round(coalesce(vl_desconto_p,0),coalesce(vl_saldo_titulo_w,0)) * 100;

	end if;
end if;

return vl_retorno_calc_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
