CREATE OR REPLACE FUNCTION obter_valor_desp_viagem (nr_seq_relat_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* 	'0' - Valor total das despesas
	'1' - Valor das despesas de Terceiro
	'2' - Valor das despesas da nossa empresa
	'3' - Soma das despesas quando o tipo de Despesa = Empresa
	'4' - Soma das despesas quando o tipo de Despesa = Viajante
*/
vl_retorno_w		double precision;
vl_total_despesa_w	double precision:=0;
vl_total_terceiro_w	double precision:=0;
vl_total_empresa_w	double precision:=0;
vl_tot_desp_empresa_w 	double precision:=0;
vl_tot_desp_viajante_w	double precision:=0;


BEGIN

select 	sum(CASE WHEN ie_responsavel_custo='T' THEN coalesce(vl_despesa,0)  ELSE 0 END ),
	sum(CASE WHEN ie_responsavel_custo='N' THEN coalesce(vl_despesa,0)  ELSE 0 END ),
	sum(coalesce(vl_despesa,0)),
	sum(CASE WHEN ie_tipo_despesa='E' THEN coalesce(vl_despesa,0)  ELSE 0 END ),
	sum(CASE WHEN ie_tipo_despesa='V' THEN coalesce(vl_despesa,0)  ELSE 0 END )
into STRICT	vl_total_terceiro_w,
	vl_total_empresa_w,
	vl_total_despesa_w,
	vl_tot_desp_empresa_w,
	vl_tot_desp_viajante_w
from 	via_despesa
where 	nr_seq_relat = nr_seq_relat_p;

if (ie_opcao_p = '0') then
	vl_retorno_w:= vl_total_despesa_w;
elsif (ie_opcao_p = '1') then
	vl_retorno_w:= vl_total_terceiro_w;
elsif (ie_opcao_p = '2') then
	vl_retorno_w:= vl_total_empresa_w;
elsif (ie_opcao_p = '3') then
	vl_retorno_w:= vl_tot_desp_empresa_w;
elsif (ie_opcao_p = '4') then
	vl_retorno_w:= vl_tot_desp_viajante_w;
end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
