CREATE OR REPLACE FUNCTION obter_valor_dominio_caso_teste (nr_sequencia_p bigint, ie_acao_p text) RETURNS varchar AS $body$
DECLARE

ds_valor_acao_w varchar(255) := '';


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_acao_p IS NOT NULL AND ie_acao_p::text <> '') then
        select Max(substr(obter_valor_dominio(7838, ie_acao_p),1,100)) Metodo
	into STRICT   ds_valor_acao_w
	from   CASO_TESTE_ACAO
	where  NR_SEQUENCIA = nr_sequencia_p
	and    IE_ACAO = ie_acao_p
	and    IE_ACAO not in (5, 14,12,17,18);
end if;

return ds_valor_acao_w;

end;
