ds_valor_dominio_w	varchar(255) := null;


BEGIN
if (cd_dominio_p IS NOT NULL AND cd_dominio_p::text <> '') and (vl_dominio_p IS NOT NULL AND vl_dominio_p::text <> '') then
	begin
