CREATE OR REPLACE FUNCTION obter_valor_ds_resultado_exame (nr_seq_exame_p bigint, nr_seq_prescr_p bigint, nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

qt_resultado_w	double precision;
ds_resultado_w	varchar(2000);


BEGIN

select 	max(coalesce(qt_resultado,pr_resultado)),
	max(coalesce(ds_resultado,ds_observacao))
into STRICT	qt_resultado_w,
	ds_resultado_w
from	exame_lab_resultado a,
	exame_lab_result_item b
where	a.nr_seq_resultado = b.nr_seq_resultado
and	nr_seq_exame 	= nr_seq_exame_p
and	nr_seq_prescr	= nr_seq_prescr_p
and	nr_prescricao	= nr_prescricao_p;

if (ie_opcao_p = 'V') then

	return 	qt_resultado_w;

elsif (ie_opcao_p = 'D') then

	return 	ds_resultado_w;

end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
